mapper.select:
SELECT

FROM recruit_system_menu a

    a.del_flag = #{DEL_FLAG_NORMAL}


        and a.title = #{title}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM recruit_system_menu a

    a.del_flag = #{DEL_FLAG_DELETE}


        and a.title = #{title}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC
