mapper.select:
SELECT

FROM recruit_system_role a

    a.del_flag = #{DEL_FLAG_NORMAL}


        and a.name = #{name}


        and a.english_name = #{englishName}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM recruit_system_role a

    a.del_flag = #{DEL_FLAG_DELETE}


        and a.name = #{name}


        and a.english_name = #{englishName}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC
